function App() {

    return (
      <Router>
        <div className="nav">
          <div className="nav-sub">
            <Link to="/" className="item"> About</Link>
            <Link to="/film" className="item"> Film</Link>
            <Link to="/project" className="item"> Project</Link>
          </div>

        </div>
        <Route

                    render={({ location }) => {
                      const{ pathname } = location;
                      return (
                    // <PageContainer>
                      <TransitionGroup compononet={null}>
                        <CSSTransition                   timeout={{
                    enter: 1000,
                    exit: 1000,
                  }} classNames="page" key={ pathname } 
                        >
                                            <Route
                    location={location}
                    render={() => (
                          <Switch>
                            <Route exact path="/" component={About}/>
                            <Route path="/film" component={Film}/>
                            <Route path="/project" component={Project}/>
                          </Switch>
                                              )}
                                              />
                          </CSSTransition>
                      </TransitionGroup>
                  // </PageContainer>

                           )}}
                           />
      </Router>
      );
}




export default App;
