function App() {

    return (
      <Router>
        <div className="nav">
          <div className="nav-sub">
            <Link to="/" className="item"> About</Link>
            <Link to="/film" className="item"> Film</Link>
            <Link to="/project" className="item"> Project</Link>
          </div>

        </div>
        <Route

                    render={({ location }) => (
                    <PageContainer>
                      <TransitionGroup compononet={null}>
                        <CSSTransition timeout={300} classNames="page" key={location.key}
                        >

                          <Switch>
                            <Route exact path="/" component={About}/>
                            <Route path="/film" component={Film}/>
                            <Route path="/project" component={Project}/>
                          </Switch>
                          </CSSTransition>
                      </TransitionGroup>
                  </PageContainer>



                           )}
                           />
      </Router>
      );
}




export default App;
