import React from "react"
import FilmCard from './FilmCard.js'
import './App.css';
import Page from './Page';

import art from './images/art.png'
import eop from './images/eop.png'
import rem from './images/rem.png'
import luk from './images/luk.jpeg'
import merlot from './images/merlot.jpeg'
import nyc from './images/nyc.png'
import vice from './images/vice.png'
import cnn from './images/cnn.png'


const films = [
    {link:"/proj5", image:merlot, title:"merlot", category:"narrative short"},
    {link:"/proj4", image:luk, title:"Strawberry Generations", category:"music video"},
    {link:"/proj1", image:art, title:"Mystery", category:"personal"},
    {link:"/proj3", image:eop, title:"en otro paraiso", category:"narrative short"},
    {link:"/proj2", image:rem, title:"R.E.M.", category:"narrative short"},
    {link:"/proj6", image:nyc, title:"nyc", category:"personal"},
    {link:"/proj7", image:vice, title:"vice", category:"commercial", src:"https://vimeo.com/360725411"},
    {link:"/proj8", image:cnn, title:"cnn", category:"commercial", src:"https://vimeo.com/361223832"} ,
]

function FilmDisplayer(props) {
  if (!props.show) {
    return null;
  }
  return (
    <div className="film-displayer">
      Hello!
    </div>
  )
}


// function Film(props) {
class Film extends React.Component {
  constructor(props) {
    super(props);
    this.selectItem = this.selectItem.bind(this)
    this.state = {
      showFilm: false,
      src: '',
      title: '',
      description: ''
    }

  }



  renderList(items) {
    var list = items.map(item => { 
      return <FilmCard  handler={this.selectItem} link={item.link} image = {item.image} title={item.title} category={item.catergory}/>
    });
    return list;
  }

  selectItem(item) {
    this.setState({
      showFilm: true,
      src: item.image
    })
    console.log(this.state.src)
  }

  render() { return(
    <Page className="film-page">
      <h1>Film</h1>
      <FilmDisplayer show={this.state.showFilm} />
      <div className="film-container">
        {this.renderList(films)}
      </div>
    </Page>
  )}
}


export default Film;
