import React from "react"
import FilmCard from './FilmCard.js'
import './App.css';
import Page from './Page';

import art from './images/art.png'
import eop from './images/eop.png'
import rem from './images/rem.png'
import luk from './images/luk.jpeg'
import merlot from './images/merlot.jpeg'
import nyc from './images/nyc.png'
import vice from './images/vice.png'
import cnn from './images/cnn.png'
import Vimeo from '@u-wave/react-vimeo';

const films = [
    {image:merlot, title:"Merlot Doesn't Go with Seabass", category:"Narrative Short" , src:"https://vimeo.com/380157225", description: "role: director, writer", portfolio:false},
    {image:luk, title:"Strawberry Generation", category:"Music Video" , src:"https://vimeo.com/380157225"},
    {image:art, title:"Mystery", category:"Experimental", src:"https://vimeo.com/380157225"},
    {image:eop, title:"En Otro Paraiso", category:"Narrative Short", src:"https://vimeo.com/380157225"},
    { image:rem, title:"R.E.M.", category:"Narrative Short", src:"https://vimeo.com/331576114"},
    { image:nyc, title:"From Brooklyn to Queens", category:"Experimental", src:"https://vimeo.com/374696154"},
    {image:vice, title:"Vice // The Artist", category:"Commercial", src:"https://vimeo.com/360725411"},
    { image:cnn, title:"CNN // Explore the Extraordinary", category:"Commercial", src:"https://vimeo.com/361223832"} ,
]

function FilmDisplayer(props) {
  if (!props.show) {
    return null;
  }
  return (
    <div className="film-displayer">
      <div className="film-displayer-video-container">
          <Vimeo className="film-displayer-vimeo"
            video={props.src} 
            autoplay
            volume={100}
            responsive
            showTitle="false"
            showPortrait="false"
            showByline="false"
            controls='false'
            width={500}

          />
        {console.log(props.src)}
      </div>
      <div className="film-displayer-text">  
        <h1> {props.title}</h1>
        <h2> {props.category}</h2>
        <p>{props.description}</p>

      </div>
    </div>
  )
}


// function Film(props) {
class Film extends React.Component {
  constructor(props) {
    super(props);
    this.selectItem = this.selectItem.bind(this)
    this.state = {
      showFilm: false,
      intervalId:0,
      src: '',
      category: '',
      title: '',
      description: ''
    }

  }


  renderList(items) {
    var list = items.map(item => { 
      return <FilmCard  handler={this.selectItem} link={item.link} image = {item.image} title={item.title} category={item.category} src={item.src} description={item.description}/>
    });
    return list;
  }

  selectItem(item) {
    this.setState({
      showFilm: true,
      src: item.src,
      title: item.title,
      category: item.category,
      description: item.description
    })
    this.scrollToTop()

  }

  scrollStep() {
    if (window.pageYOffset === 0) {
        clearInterval(this.state.intervalId);
    }
    window.scroll(0, window.pageYOffset - "50");
  }
  scrollToTop() {
    let intervalId = setInterval(this.scrollStep.bind(this), "16.66");
    this.setState({ intervalId: intervalId });
  }

  render() { return(
    <Page className="film-page">
      <FilmDisplayer show={this.state.showFilm} src={this.state.src} title={this.state.title} category={this.state.category} />


          <h1>Films</h1>
        <div className="film-container">
        {this.renderList(films)}
      </div>
    </Page>
  )}
}


export default Film;
