import React from "react"
import FilmCard from './FilmCard.js'
import './App.css';
import Page from './Page';

import art from './images/art.png'
import eop from './images/eop.png'
import rem from './images/rem.png'
import luk from './images/luk.jpeg'
import merlot from './images/merlot.jpeg'
import nyc from './images/nyc.png'
import vice from './images/vice.png'
import cnn from './images/cnn.png'


const films = [
    {image:merlot, title:"merlot", category:"narrative short" , src:"https://vimeo.com/360725411"},
    {image:luk, title:"Strawberry Generations", category:"music video" , src:"https://vimeo.com/360725411"},
    {image:art, title:"Mystery", category:"personal", src:"https://vimeo.com/360725411"},
    {image:eop, title:"en otro paraiso", category:"narrative short", src:"https://vimeo.com/360725411"},
    { image:rem, title:"R.E.M.", category:"narrative short", src:"https://vimeo.com/360725411"},
    { image:nyc, title:"nyc", category:"personal", src:"https://vimeo.com/360725411"},
    {image:vice, title:"vice", category:"commercial", src:"https://vimeo.com/360725411"},
    { image:cnn, title:"cnn", category:"commercial", src:"https://vimeo.com/361223832"} ,
]

function FilmDisplayer(props) {
  if (!props.show) {
    return null;
  }
  return (
    <div className="film-displayer">
      {props.title}
    </div>
  )
}


// function Film(props) {
class Film extends React.Component {
  constructor(props) {
    super(props);
    this.selectItem = this.selectItem.bind(this)
    this.state = {
      showFilm: false,
      intervalId:0,
      src: '',
      title: '',
      description: ''
    }

  }


  renderList(items) {
    var list = items.map(item => { 
      return <FilmCard  handler={this.selectItem} link={item.link} image = {item.image} title={item.title} category={item.category} src={item.src}/>
    });
    return list;
  }

  selectItem(item) {
    this.setState({
      showFilm: true,
      src: item.src,
      title: item.title
    })
    this.scrollToTop()

  }

  scrollStep() {
    if (window.pageYOffset === 0) {
        clearInterval(this.state.intervalId);
    }
    window.scroll(0, window.pageYOffset - "50");
  }
  scrollToTop() {
    let intervalId = setInterval(this.scrollStep.bind(this), "16.66");
    this.setState({ intervalId: intervalId });
  }

  render() { return(
    <Page className="film-page">
      <h1>Film</h1>
      <FilmDisplayer show={this.state.showFilm} src={this.state.src} title={this.state.title} />
      <div className="film-container">
        {this.renderList(films)}
      </div>
    </Page>
  )}
}


export default Film;
